import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8083';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json'
  },
  timeout: 30000
});

// Interceptor para agregar token si existe
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Interceptor para manejar errores de autenticación
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Token inválido o expirado
      localStorage.removeItem('token');
      localStorage.removeItem('usuario');
      if (window.location.pathname !== '/login') {
        window.location.href = '/login';
      }
    }
    return Promise.reject(error);
  }
);

export default api;

export const getProductos = () => api.get('/productos');
export const getProductoPorId = (id) => api.get(`/productos/${id}`);
export const getProductoPorCodigo = (codigo) => api.get(`/productos/codigo/${codigo}`);
export const crearProducto = (payload) => api.post('/productos', payload);
export const actualizarProducto = (id, payload) => api.put(`/productos/${id}`, payload);
export const eliminarProducto = (id) => api.delete(`/productos/${id}`);

// Endpoints de Usuarios
export const getUsuarios = () => api.get('/api/usuarios');
export const getUsuarioPorId = (id) => api.get(`/api/usuarios/${id}`);
export const crearUsuario = (payload) => api.post('/api/usuarios', payload);
export const actualizarUsuario = (id, payload) => api.put(`/api/usuarios/${id}`, payload);
export const eliminarUsuario = (id) => api.delete(`/api/usuarios/${id}`);

// Endpoints de Órdenes
export const getOrdenes = () => api.get('/api/ordenes');
export const getOrdenPorId = (id) => api.get(`/api/ordenes/${id}`);
export const crearOrden = (payload) => api.post('/api/ordenes', payload);
export const actualizarOrden = (id, payload) => api.put(`/api/ordenes/${id}`, payload);
export const eliminarOrden = (id) => api.delete(`/api/ordenes/${id}`);

// Endpoints de Autenticación
export const login = (credentials) => api.post('/api/auth/login', credentials);
export const registro = (userData) => api.post('/api/auth/registro', userData);
export const logout = () => api.post('/api/auth/logout');
