import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8083';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json'
  },
  timeout: 10000
});

// Interceptor para agregar token si existe
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export default api;

export const getProductos = () => api.get('/api/productos');
export const getProductoPorCodigo = (codigo) => api.get(`/api/productos/codigo/${codigo}`);
export const crearProducto = (payload) => api.post('/api/productos', payload);
export const actualizarProducto = (id, payload) => api.put(`/api/productos/${id}`, payload);
