import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { registrarLogAdmin } from '../utils/logManager';
import ModalConfirmacion from '../components/ModalConfirmacion';
import { getProductos, eliminarProducto as eliminarProductoAPI } from '../utils/api';
import '../styles/Admin.css';

export default function AdminProductos() {
  const [productos, setProductos] = useState([]);
  const [busqueda, setBusqueda] = useState('');
  const [mostrarModal, setMostrarModal] = useState(false);
  const [productoAEliminar, setProductoAEliminar] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    cargarProductos();
  }, []);

  const cargarProductos = async () => {
    try {
      const response = await getProductos();
      const productosBackend = response.data;

      const mapearCategoria = (categoriaId) => {
        const categorias = {
          1: 'Consolas',
          2: 'Videojuegos',
          3: 'Accesorios',
          4: 'Juegos de Mesa',
          5: 'Figuras',
          6: 'Otros'
        };
        return categorias[categoriaId] || 'Otros';
      };

      const productosFormateados = productosBackend.map(p => ({
        id: p.codigo || p.id,
        codigo: p.codigo,
        categoria: mapearCategoria(p.categoriaId),
        nombre: p.nombre,
        precio: p.precioVenta || p.precio || 0,
        stock: p.stockActual || p.stock || 0,
        descripcion: p.descripcion || '',
        imagen: p.imagenUrl || p.imagen || '/assets/imgs/destacado1.png',
        marca: p.marca
      }));

      setProductos(productosFormateados);
    } catch (error) {
      console.error('Error al cargar productos desde API:', error);
      if (window.notificar) {
        window.notificar('Error al cargar productos. Verifica que el servidor esté activo.', 'error', 3000);
      }
    }
  };

  const confirmarEliminar = (codigo) => {
    setProductoAEliminar(codigo);
    setMostrarModal(true);
  };

  const eliminarProducto = async () => {
    if (productoAEliminar) {
      const producto = productos.find(p => p.codigo === productoAEliminar);
      
      try {
        // Buscar el ID real del producto en el backend
        const productoCompleto = productos.find(p => p.codigo === productoAEliminar);
        if (!productoCompleto || !productoCompleto.id) {
          throw new Error('No se encontró el ID del producto');
        }

        // Eliminar del backend
        await eliminarProductoAPI(productoCompleto.id);
        
        // Actualizar la lista local
        const productosActualizados = productos.filter(p => p.codigo !== productoAEliminar);
        setProductos(productosActualizados);
        
        registrarLogAdmin(`Eliminó producto: ${producto?.nombre || 'Desconocido'} (${productoAEliminar})`);
        
        if (window.notificar) {
          window.notificar('Producto eliminado exitosamente', 'success', 3000);
        }
      } catch (error) {
        console.error('Error al eliminar producto:', error);
        if (window.notificar) {
          window.notificar('Error al eliminar el producto. Intenta nuevamente.', 'error', 3000);
        }
      } finally {
        setMostrarModal(false);
        setProductoAEliminar(null);
      }
    }
  };

  const cancelarEliminar = () => {
    setMostrarModal(false);
    setProductoAEliminar(null);
  };

  const editarProducto = (codigo) => {
    navigate(`/admin/productos/editar/${codigo}`);
  };

  const productosFiltrados = productos.filter(p => 
    p.nombre.toLowerCase().includes(busqueda.toLowerCase()) ||
    p.codigo.toLowerCase().includes(busqueda.toLowerCase()) ||
    p.categoria.toLowerCase().includes(busqueda.toLowerCase())
  );

  const productosSinStock = productos.filter(p => p.stock === 0);

  return (
    <main className="container admin-page">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h2 className="section-title mb-2">Gestión de Productos</h2>
          <Link to="/admin" className="text-secondary">
            ← Volver al Panel
          </Link>
        </div>
        <Link to="/admin/productos/nuevo" className="btn btn-success">
          + Nuevo Producto
        </Link>
      </div>

      {productosSinStock.length > 0 && (
        <div className="alert alert-warning d-flex align-items-center mb-4" role="alert">
          <strong>⚠️ Atención:</strong>
          <span className="ms-2">
            Hay {productosSinStock.length} producto{productosSinStock.length > 1 ? 's' : ''} sin stock. 
            Los usuarios no podrán verlos hasta que agregues más unidades.
          </span>
        </div>
      )}

      <div className="mb-4">
        <input
          type="text"
          className="form-control admin-search-input"
          placeholder="Buscar por nombre, código o categoría..."
          value={busqueda}
          onChange={(e) => setBusqueda(e.target.value)}
        />
      </div>

      {productos.length === 0 ? (
        <div className="text-center py-5">
          <p className="text-secondary mb-4">No hay productos registrados</p>
          <Link to="/admin/productos/nuevo" className="btn btn-success">
            Agregar Primer Producto
          </Link>
        </div>
      ) : (
        <div className="admin-table">
          <table className="table table-dark table-hover">
            <thead>
              <tr>
                <th>Imagen</th>
                <th>Código</th>
                <th>Nombre</th>
                <th>Categoría</th>
                <th>Precio</th>
                <th>Stock</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {productosFiltrados.map((producto) => (
                <tr key={producto.codigo}>
                  <td>
                    <img 
                      src={producto.imagen || '/assets/icons/icono.png'} 
                      alt={producto.nombre}
                      width="50"
                      height="50"
                      className="admin-producto-img"
                    />
                  </td>
                  <td>{producto.codigo}</td>
                  <td>{producto.nombre}</td>
                  <td>
                    <span className="badge bg-secondary">
                      {producto.categoria}
                    </span>
                  </td>
                  <td className="admin-producto-precio">
                    ${producto.precio.toLocaleString('es-CL')}
                  </td>
                  <td>
                    <span className={producto.stock < 5 ? 'text-danger' : 'text-success'}>
                      {producto.stock}
                    </span>
                    {producto.stock === 0 && (
                      <span className="badge bg-danger ms-2">Sin Stock</span>
                    )}
                  </td>
                  <td>
                    <button 
                      className="btn btn-sm btn-success btn-action"
                      onClick={() => editarProducto(producto.codigo)}
                    >
                      Editar
                    </button>
                    <button 
                      className="btn btn-sm btn-danger btn-action"
                      onClick={() => confirmarEliminar(producto.codigo)}
                    >
                      Eliminar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {productosFiltrados.length === 0 && productos.length > 0 && (
        <div className="text-center py-4">
          <p className="text-secondary">No se encontraron productos con "{busqueda}"</p>
        </div>
      )}

      <ModalConfirmacion
        mostrar={mostrarModal}
        titulo="Eliminar Producto"
        mensaje={`¿Estás seguro de que deseas eliminar el producto ${productoAEliminar}? Esta acción no se puede deshacer.`}
        onConfirmar={eliminarProducto}
        onCancelar={cancelarEliminar}
      />
    </main>
  );
}
