import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCarrito } from '../context/CarritoContext';
import '../styles/Home.css';

const PRODUCTOS_DEFAULT = [
  {
    codigo: "JM001",
    categoria: "Juegos de Mesa",
    nombre: "Catan",
    precio: 29990,
    stock: 10,
    desc: "Juego clásico de estrategia.",
    img: "/assets/imgs/destacado1.png",
    imagen: "/assets/imgs/destacado1.png"
  },
  {
    codigo: "AC001",
    categoria: "Accesorios",
    nombre: "Control Xbox Series X",
    precio: 59990,
    stock: 15,
    desc: "Control inalámbrico.",
    img: "/assets/imgs/destacado2.png",
    imagen: "/assets/imgs/destacado2.png"
  },
  {
    codigo: "CO001",
    categoria: "Consolas",
    nombre: "PlayStation 5",
    precio: 549990,
    stock: 5,
    desc: "Consola de última generación.",
    img: "/assets/imgs/destacado3.png",
    imagen: "/assets/imgs/destacado3.png"
  }
];

export default function Home() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [productos, setProductos] = useState(PRODUCTOS_DEFAULT);
  const { agregarAlCarrito } = useCarrito();
  const navigate = useNavigate();
  
  const itemsPorSlide = 3;
  const totalSlides = Math.ceil(productos.length / itemsPorSlide);

  useEffect(() => {
    const destacadosCodigosLS = JSON.parse(localStorage.getItem('destacados') || '[]');
    if (destacadosCodigosLS.length > 0) {
      const productosLS = JSON.parse(localStorage.getItem('productos') || '[]');
      const productosDestacados = destacadosCodigosLS.map(codigo => {
        const producto = productosLS.find(p => p.codigo === codigo);
        if (producto && producto.stock > 0) {
          return {
            codigo: producto.codigo,
            categoria: producto.categoria,
            nombre: producto.nombre,
            precio: producto.precio,
            stock: producto.stock,
            desc: producto.descripcion || producto.desc || 'Producto destacado',
            img: producto.imagen,
            imagen: producto.imagen
          };
        }
        return null;
      }).filter(p => p !== null);
      
      if (productosDestacados.length > 0) {
        setProductos(productosDestacados);
      }
    }
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % totalSlides);
    }, 5000);
    return () => clearInterval(interval);
  }, [totalSlides]);

  const handlePrev = () => {
    setCurrentSlide((prev) => (prev - 1 + totalSlides) % totalSlides);
  };

  const handleNext = () => {
    setCurrentSlide((prev) => (prev + 1) % totalSlides);
  };

  const handleAgregarAlCarrito = (codigo) => {
    const producto = productos.find(p => p.codigo === codigo);
    if (producto) {
      agregarAlCarrito(producto);
      if (window.notificar) {
        window.notificar(`¡${producto.nombre} agregado al carrito!`, 'success', 3000);
      }
    }
  };

  return (
    <>
      <section className="hero">
        <div className="container">
          <h1>¡DESAFÍA TUS LÍMITES!</h1>
          <p>Explora consolas, accesorios, PCs y más</p>
          <Link className="btn btn-secondary" to="/productos">Explorar Productos</Link>
          <Link className="btn btn-secondary" to="/nosotros" style={{ marginLeft: '.5rem' }}>Conócenos</Link>
        </div>
      </section>

      <main className="container" style={{ marginTop: '2rem' }}>
        <h2 className="section-title">Destacados</h2>
        
        <div id="productos-carrusel-container" className="mt-5">
          <div id="productosCarrusel" className="carousel slide" data-bs-ride="carousel">
            <div className="carousel-inner" id="carousel-productos-inner">
              {Array.from({ length: totalSlides }).map((_, slideIdx) => {
                const startIdx = slideIdx * itemsPorSlide;
                const slideProductos = productos.slice(startIdx, startIdx + itemsPorSlide);
                
                return (
                  <div className={`carousel-item${slideIdx === currentSlide ? ' active' : ''}`} key={slideIdx}>
                    <div className="row g-4 justify-content-center p-4">
                      {slideProductos.map((prod) => (
                        <div className="col-md-4" key={prod.codigo}>
                          <div className="card h-100 bg-dark text-white" style={{ border: '1px solid #333' }}>
                            <img 
                              src={prod.img} 
                              alt={prod.nombre} 
                              className="card-img-top p-3" 
                              style={{ 
                                height: '250px',
                                objectFit: 'contain', 
                                background: '#222',
                                cursor: 'pointer'
                              }}
                              onClick={() => navigate(`/detalle/${prod.codigo}`)}
                            />
                            <div className="card-body d-flex flex-column">
                              <h5 
                                className="card-title text-neon mb-2" 
                                style={{ cursor: 'pointer', fontSize: '1.25rem' }}
                                onClick={() => navigate(`/detalle/${prod.codigo}`)}
                              >
                                {prod.nombre}
                              </h5>
                              <span className="badge bg-secondary mb-2 align-self-start">{prod.categoria}</span>
                              <p className="card-text text-success fw-bold mb-2" style={{ fontSize: '1.3rem' }}>
                                ${prod.precio.toLocaleString('es-CL')}
                              </p>
                              <p className="card-text mb-3 flex-grow-1">{prod.desc}</p>
                              <button 
                                className="btn btn-success w-100 mt-auto" 
                                onClick={() => handleAgregarAlCarrito(prod.codigo)}
                              >
                                Agregar al carrito
                              </button>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                );
              })}
            </div>
            <button 
              className="carousel-control-prev" 
              type="button" 
              onClick={handlePrev}
            >
              <span className="carousel-control-prev-icon" aria-hidden="true"></span>
              <span className="visually-hidden">Anterior</span>
            </button>
            <button 
              className="carousel-control-next" 
              type="button" 
              onClick={handleNext}
            >
              <span className="carousel-control-next-icon" aria-hidden="true"></span>
              <span className="visually-hidden">Siguiente</span>
            </button>
          </div>
        </div>
      </main>
    </>
  );
}
